\documentclass{article}
\usepackage{tikz}
\usepackage{amsmath}

\begin{document}

\begin{tikzpicture}

% First small 2x2 grid with b_i
\draw (0,0) grid (2,2);
\node at (0.5,1.5) {$b_1$};
