\draw[dashed arrow] (abcd) to[bend right=20] (bc);
\draw[dashed arrow] (ab) to[out=240,in=180] (abcd);
\draw[dashed arrow] (hd) to[out=30,in=150] node[above] {$\emptyset$} (cd);

