\documentclass{standalone}
\usepackage{tikz}
\usetikzlibrary{calc}

\begin{document}

\begin{tikzpicture}[
    cell/.style={rectangle, draw=gray, minimum size=0.5cm},
    caption/.style={below, node distance=1pt, font=\footnotesize}]

    % Define the grid size
    \def\gridSize{8}

