        \foreach \y in {1,...,8} {
            \pgfmathrandominteger{\a}{1}{100}
            \ifnum\a > 50
                \node[cell, fill=gray!50] at (\x, \y) {};
            \else
                \node[cell] at (\x, \y) {};
            \fi
        }
    }

    % Block Random Sparsity
