        \foreach \y in {-3, -2, -1, 0, 1, 2} {
            \draw[gray] (0, \y) -- (3, \y);
        }

        % Arrows and labels
        % Left boundary
        \foreach \y/\color in {2/cyan, 1/green, 0/orange, -1/red, -2/red!50!black} {
            \draw[->, \color, thick] (0, \y) -- (-0.5, \y) node[left] {\y};
        }
        \node[left] at (0, -3) {$(0, -3)$};

        % Colored arrows
        \draw[->, green, thick] (0,1) -- (1,1);
        \draw[->, cyan, thick] (0,2) -- (1,2);
