  % Model
  \node[model] (M) at (3, 0.5) {$M$};

  % Table constraint
  \node[constraints] (ext) at (3, -0.5) {$ext$};

  % Tuples
  \node[tuples] (t1) at (2, -1.5) {$t_1$};
  \node[tuples] (t2) at (4, -1.5) {$t_2$};

  %% Edges
  % Values to variables
  \draw[edge] (v1) -- (x1);
  \draw[edge] (v2) -- (x1);
  \draw[edge] (v2) -- (x2);
  \draw[edge] (v3) -- (x2);

  % Inequality structure
  \draw[edge] (x1) -- (lhs1);
  \draw[edge] (lhs1) -- (leq);
  \draw[edge] (x2) -- (rhs1);
  \draw[edge] (rhs1) -- (leq);
  \draw[edge] (leq) -- (M);

  % Table constraint structure
  \draw[edge] (t1) -- (ext);
  \draw[edge] (t2) -- (ext);
  \draw[edge] (x1) -- (ext);
  \draw[edge] (x2) -- (ext);
  \draw[edge] (M) -- (ext);
  \draw[edge] (M) -- (x1);

  %% Highlighting constraint areas
  \begin{scope}[on background layer]
    \node[highlight, fit=(lhs1) (leq) (rhs1), label={[anchor=north, yshift=5mm]{\small$3x_1 \leq 4x_2$}}] {};
  \end{scope}

\end{tikzpicture}
\end{document}
