\documentclass{standalone}
\usepackage{tikz}
\usetikzlibrary{arrows.meta, positioning, quotes}

\begin{document}
\begin{tikzpicture}[
    node distance=3cm and 3cm,
    vertex/.style={circle, draw, minimum size=1.5cm},
