\node[vertex, above right=of A] (B) {$0\ 0\ 0\ 0$};
\node[vertex, below right=of B] (C) {$0\ 0\ 0\ 1$};
\node[vertex, below right=of A] (D) {$0\ 0\ 1\ 1$};
\node[vertex, right=of C] (E) {$0\ 1\ 0\ 1$};
\node[vertex, left=of D] (F) {$1\ 1\ 1\ 1$};

