\documentclass[tikz,border=3.14mm]{standalone}

\begin{document}

\begin{tikzpicture}
    % Define styles
    \tikzset{
        setA/.style={fill=cyan!30, draw=blue, thick},
        setB/.style={fill=magenta!30, draw=magenta, thick},
        setAB/.style={fill=blue!30!magenta!50, draw=black, thick},
        textStyle/.style={font=\small, text=blue}
    }

    % Define coordinates for the sets
    \coordinate (A) at (0,0);
    \coordinate (B) at (6,0);
    \coordinate (AB) at (3,-2);

    % Draw the sets
    \draw[setA] (A) ellipse (4cm and 2cm) node[below] {Eulerian sources};
    \draw[setB] (B) ellipse (4cm and 2cm) node[below] {Lagrangian sources associated to a given $\chi$};
    \draw[setAB] (AB) ellipse (2cm and 1cm) node[below] {Broad sources};

    % Draw the universal set
    \draw[thick] (-4.5,-2.5) rectangle (10.5,1.5) node[midway] {Borel, bounded functions};

    % Add text inside the intersection
    \node[setAB, textStyle] at (AB) {\S \ref{sec:??}};
    \node[setAB, textStyle] at (intersection of A--B and AB--(AB-|A)) {\S \ref{sec:??}};
\end{tikzpicture}

\end{document}
