\documentclass[tikz,border=3.14mm]{standalone}
\usetikzlibrary{shapes.geometric,arrows.meta,positioning,quotes}
\begin{document}
\tikzset{
    amp/.style={triangle 45, draw, fill=black, minimum width=1.5cm, minimum height=1.5cm},
    bs/.style={rectange, draw, fill=black, minimum width=1.5cm, minimum height=4cm},
    state/.style={minimum width=1cm, minimum height=1cm},
    tensor/.style={circle, draw, fill=white, minimum width=0.7cm},
}
\begin{tikzpicture}
    \node (rho) [state] {$\rho$};
    \node (amp) [amp, right=2cm of rho] {Amp};
    \node (bs) [bs, right=1cm of amp] {B.S.};
    \foreach \i [evaluate=\i as \j using {int(\i+1)}] in {1,2,...,4}
    {
        \node (rho\i) [tensor, right=0.75cm of bs.east|-rho\j] {$\rho_{\i}$};
    }
    \draw [-{Latex[length=3mm]}] (rho) -- (amp);
    \draw [-{Latex[length=3mm]}] (amp) -- (bs);
    \draw [-{Latex[length=3mm]}, dotted] (rho2) -- (rho1);
    \draw [-{Latex[length=3mm]}, dotted] (rho3) -- (rho2);
