\begin{document}
\begin{tikzpicture}[
    every node/.style={draw, circle, inner sep=0pt, minimum size=0.5cm},
    grow via three points={one child at (0.7,-0.5) and two children at (0.7,-0.5) and (0.7,-1)},
    every child/.style={draw, edge from parent/.style={draw}}
