\node[tuple] (t1) at (4,1.5) {$(1,2)$};
\node[tuple] (t2) at (6,1.5) {$(2,3)$};
\draw (table) edge (t1) edge (t2);
\draw (t1) edge (x1) edge (x2);
\draw (t2) edge (x1) edge (x2);

% Model vertex
