    }

    % Labels
    \node[blue,above right] at (1.5,0.8) {$\Gamma_\lambda$};
    \node[blue] at (1.2,0.4) {$D_\lambda$};
    \node[red] at (1,-0.1) {$\sigma(T) = \sigma(T_X)$};
\end{tikzpicture}
\end{document}
