\documentclass{standalone}
\usepackage{tikz}
\usepackage{pgfplots}
\usetikzlibrary{patterns,shapes.arrows,positioning,calc}
\pgfplotsset{compat=1.17}

\definecolor{arr2flatcolour}{rgb}{0.0, 0.0, 1.0}
\definecolor{minnesotacolour}{rgb}{1.0, 0.84, 0.0}
\definecolor{gaussiancolour}{rgb}{0.0, 0.5, 0.0}
