    \node at (7, -4.5) {Block Random Column Sparsity};
    \foreach \j in {0,...,\cols} {
        \ifnum \j<3
            \foreach \i in {0,...,\rows} {
                \fill[gray] (\j*\cellsize+5, -\i*\cellsize-5) rectangle ++(\cellsize, \cellsize);
            }
        \fi
        \foreach \i in {0,...,\rows} {
            \draw (\j*\cellsize+5, -\i*\cellsize-5) rectangle ++(\cellsize, \cellsize);
        }
    }
\end{tikzpicture}
\end{document}
