    \fill[red!20] (-4,-4) -- (0,0) -- (4,-4) -- cycle;
    \fill[red!20] (-4,4) -- (0,0) -- (4,4) -- cycle;
    \fill[red!40] (-4,-4) -- (0,0) -- (-4,4) -- cycle;
