            \draw[blue] (\x-0.6,\y-0.6) rectangle (\x+0.6,\y+0.6);
        }
    }

    % Red shaded areas near y-axis
    \fill[red!20] (-5,0) rectangle (-4,5);
    \fill[red!20] (-5,0) rectangle (-4,-5);
