Here is a directly compilable LaTeX document that recreates the shown figure using TikZ/PGFPlots. It places a shared framed legend above two side-by-side axes, uses a y-scale factor “·10^4”, custom colors, gridlines, and titles/labels exactly as in the image. The curves are smooth proxies for the histograms to match the appearance.

\documentclass[tikz,border=4pt]{standalone}
\usepackage{pgfplots}
\pgfplotsset{compat=1.18}
\usetikzlibrary{pgfplots.groupplots}
\usepackage{xcolor}
\usepackage{caption}

% Custom colors to match the figure
\definecolor{brightgreen}{RGB}{0,180,0}
\definecolor{skyblue}{RGB}{0,160,255}
\definecolor{brownish}{RGB}{150,100,40}

\begin{document}
\begin{tikzpicture}
  % Shared legend to be placed above both plots
  \pgfplotsset{
    mygroup/.style={
      group style={
        group size=2 by 1,
        horizontal sep=2.1cm,
        vertical sep=1.0cm
      },
      width=7.0cm,
      height=5.0cm,
      xmin=-18, xmax=-6,
      ymin=0,   ymax=15000,
      xtick={-18,-12,-6},
      xticklabels={$10^{-18}$,$10^{-12}$,$10^{-6}$},
      ytick={0,5000,10000,15000},
      grid=both,
      major grid style={gray!45},
      minor grid style={gray!20},
      minor x tick num=1,
      tick align=outside,
      axis line style={black},
      tick style={black},
      scaled y ticks=base 10:4,
      ytick scale label code/.code={$\cdot 10^{#1}$},
      title style={font=\large, yshift=1pt},
      label style={font=\normalsize},
      legend to name=stabilitylegend,
      legend columns=2,
      legend style={
        draw=black, fill=white,
        font=\small,
        nodes={inner xsep=5pt, inner ysep=4pt},
        column sep=1.4em
      }
    }
  }

  \begin{groupplot}[mygroup]

    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
    % Left axis: Rotation stability
    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
    \nextgroupplot[
      title={Rotation stability},
      xlabel={$\log_{10}$ of rotation error},
      ylabel={Frequency}
    ]
      % P2L1 our (green, thick)
      \addplot[brightgreen, very thick, smooth, samples=250, domain=-18:-6]
        {14500*exp(-0.5*((x+15.7)/0.45)^2)};
      \addlegendentry{P2L1 our}

      % P2L1 3Q3 (red, solid)
      \addplot[red, thick, smooth, samples=250, domain=-18:-6]
        {9500*exp(-0.5*((x+15.4)/0.60)^2)};
      \addlegendentry{P2L1 3Q3}

      % P2L1 Ram.+SVD (red, dashed)
      \addplot[red, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6]
        {9000*exp(-0.5*((x+15.1)/0.70)^2)};
      \addlegendentry{P2L1 Ram.+SVD}

      % P2L1 Ram.+LU (brown, dashed)
      \addplot[brownish, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6]
        {8000*exp(-0.5*((x+14.8)/0.80)^2)};
      \addlegendentry{P2L1 Ram.+LU}

      % P1L2 our (cyan/skyblue, solid)
      \addplot[skyblue, thick, smooth, samples=250, domain=-18:-6]
        {10000*exp(-0.5*((x+15.2)/0.65)^2)};
      \addlegendentry{P1L2 our}

      % P1L2 3Q3 (black, dashed)
      \addplot[black, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6]
        {8700*exp(-0.5*((x+15.0)/0.75)^2)};
      \addlegendentry{P1L2 3Q3}

    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
    % Right axis: Translation stability
    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
    \nextgroupplot[
      title={Translation stability},
      xlabel={$\log_{10}$ of translation error}
    ]
      % The same styles re-drawn (no legend entries here)
      \addplot[brightgreen, very thick, smooth, samples=250, domain=-18:-6, forget plot]
        {11200*exp(-0.5*((x+14.2)/0.55)^2)};
      \addplot[red, thick, smooth, samples=250, domain=-18:-6, forget plot]
        {7800*exp(-0.5*((x+13.9)/0.70)^2)};
      \addplot[red, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6, forget plot]
        {7200*exp(-0.5*((x+13.6)/0.80)^2)};
      \addplot[brownish, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6, forget plot]
        {6500*exp(-0.5*((x+13.4)/0.85)^2)};
      \addplot[skyblue, thick, smooth, samples=250, domain=-18:-6, forget plot]
        {8000*exp(-0.5*((x+13.7)/0.75)^2)};
      \addplot[black, dashed, line width=1.4pt, smooth, samples=250, domain=-18:-6, forget plot]
        {7000*exp(-0.5*((x+13.5)/0.80)^2)};

  \end{groupplot}

  % Place the shared legend a bit above the two axes
  \path (group c1r1.north west) -- coordinate (L) (group c2r1.north east);
  \node[anchor=south] at ($(L)!0.5!(group c1r1.north east)$) {\ref{stabilitylegend}};
\end{tikzpicture}

% Caption requested by the user
\captionof{figure}{\textbf{Stability test.} Histogram of rotation (\textit{l.}), and translation (\textit{r.}) errors computed over $1e5$ noiseless samples.}
\end{document}

