Here is a directly compilable LaTeX/TikZ file that recreates the figure (left: before Fourier transform; right: after Fourier transform) with the same visual appearance, labels, sector identifications, strand numberings, basepoint, arrows, and partial formal monodromy matrices.

% !TEX program = pdflatex
\documentclass[tikz, border=3pt]{standalone}
\usepackage{amsmath, amssymb}
\usetikzlibrary{calc, decorations.markings}

\definecolor{myblue}{RGB}{40,60,255}
\definecolor{mygray}{gray}{0.55}
\definecolor{mygreen}{RGB}{10,150,70}

% Style shortcuts
\tikzset{
  stokes/.style   = {draw=myblue, line width=1.1pt},
  thinblue/.style = {myblue, font=\small},
  greennum/.style = {mygreen, font=\footnotesize},
  circnum/.style  = {circle, draw=black!60, inner sep=1pt, minimum size=11pt, font=\scriptsize},
  arrowpath/.style = {
    stokes,
    postaction={decorate},
    decoration={markings,
      mark=at position 0.18 with {\arrow{>}},
      mark=at position 0.52 with {\arrow{>}},
      mark=at position 0.86 with {\arrow{>}}
    }
  }
}

% 3-cusped (deltoid-like) curve used for the strands
%   center=(#1,#2), scale=#3
\newcommand{\Deltoid}[3]{%
  \draw[stokes, smooth, samples=400, domain=0:360, variable=\t]
    plot ({#1 + #3*(2*cos(\t) + cos(2*\t))},
          {#2 + #3*(2*sin(\t) - sin(2*\t))});
}

% Same but with arrows along the curve (used on the right panel)
\newcommand{\DeltoidArrows}[3]{%
  \draw[arrowpath, smooth, samples=400, domain=0:360, variable=\t]
    plot ({#1 + #3*(2*cos(\t) + cos(2*\t))},
          {#2 + #3*(2*sin(\t) - sin(2*\t))});
}

\begin{document}
\begin{tikzpicture}[scale=1, every node/.style={inner sep=1pt}]

%--------------------------%
% LEFT: before Fourier transform
%--------------------------%
\begin{scope}[shift={(-4.7,0)}]
  % parameters
  \coordinate (L) at (0,0);
  \def\sL{0.62}
  \def\Rcirc{1.55}

  % the 3-lobed Stokes curve
  \Deltoid{0}{0}{\sL}

  % dotted Stokes circle with sector labels
  \draw[densely dotted, mygray, line width=0.5pt] (L) circle (\Rcirc);

  % sector labels J_1,...,J_6 around the dotted circle (clockwise)
  \foreach \k/\ang in {1/0,2/-60,3/-120,4/180,5/120,6/60}{
    \node[thinblue] at ({\Rcirc*cos(\ang)},{\Rcirc*sin(\ang)}) {$J_{\k}$};
  }

  % numbers "1" along the three outer strands (blue)
  \foreach \ang in {10,130,250}{
    \node[thinblue] at ({1.18*cos(\ang)},{1.18*sin(\ang)}) {\small 1};
  }

  % Panel title (left)
  \node[anchor=north west] at (-2.45,1.95)
    {\footnotesize Left: formal local system before Fourier transform};
\end{scope}

%--------------------------%
% RIGHT: after Fourier transform
%--------------------------%
\begin{scope}[shift={(4.7,0)}]
  % parameters
  \coordinate (R) at (0,0);
  \def\sA{0.60}
  \def\sB{0.66}
  \def\sC{0.72}

  % three strands after Fourier transform (slightly offset scales)
  \DeltoidArrows{0}{0}{\sA}
  \DeltoidArrows{0}{0}{\sB}
  \DeltoidArrows{0}{0}{\sC}

  % basepoint \hat b near the right cusp of the middle strand
  \coordinate (bhat) at ({3*\sB + 0.08}, {0.16});
  \fill (bhat) circle (0.7pt);
  \node[anchor=west, font=\scriptsize] at ($(bhat)+(0.08,0.02)$) {$\widehat{b}$};

  % strand numbering (green), continued clockwise from the basepoint
  \node[greennum] at (0,1.26) {1};
  \node[greennum] at (-0.65,-0.78) {2};
  \node[greennum] at (1.35,-0.42) {1};
  \node[greennum] at (0.25,0.38) {2};

  % identification of sectors by Legendre transform (circled numbers)
  \node[circnum] at (0.30,0.95) {1};
  \node[circnum] at (-0.90,-0.25) {2};
  \node[circnum] at (1.10,-0.55) {3};

  % labels \hat J_i placed near the corresponding sectors/strands
  \node[thinblue, anchor=east]  at (-1.90, 1.10) {$\widehat{J}_{6}$};
  \node[thinblue, anchor=east]  at (-1.95,-1.05) {$\widehat{J}_{2}$};
  \node[thinblue, anchor=west]  at ( 2.10, 0.15) {$\widehat{J}_{4}$};
  \node[thinblue, anchor=south] at ( 0.20, 1.55) {$\widehat{J}_{3}$};
  \node[thinblue, anchor=north] at ( 0.10,-1.55) {$\widehat{J}_{5}$};
  \node[thinblue, anchor=west]  at ( 1.30, 0.95) {$\widehat{J}_{1}$};

  % partial formal monodromies (transformation rule; signs flip across negative strands)
  \node at (1.95,0.95)
    {$\bigl(\begin{smallmatrix} a & 1 \\[1pt] 0 & 1 \end{smallmatrix}\bigr)$};

  \node at (-2.00,0.05)
    {$\bigl(\begin{smallmatrix} -1 & 0 \\[1pt] 0 & 1 \end{smallmatrix}\bigr)$};

  \node at (1.05,-1.30)
    {$\bigl(\begin{smallmatrix} -1 & 0 \\[1pt] 0 & 1 \end{smallmatrix}\bigr)$};

  % K^{-1} indications (orientation of the gluing/Legendre kernel)
  \draw[->, black] (-0.22,0.05) -- (0.32,0.05) node[midway, above, yshift=2pt] {\scriptsize $K^{-1}$};
  \node[font=\scriptsize] at (0.45,-0.68) {$k^{-1}$};

  % Panel title (right)
  \node[anchor=north west] at (-2.55,1.95)
    {\footnotesize Right: formal local system after Fourier transform};
\end{scope}

\end{tikzpicture}
\end{document}

