\documentclass[tikz,border=3pt]{standalone}
\usetikzlibrary{calc}

\begin{document}
% Illustration for the proof of Proposition~\ref{twoexamples}.
% All inner nodes of the tree are labeled with a.
% All leaves not shown in the illustration are labeled with c.
\begin{tikzpicture}[
  every node/.style={font=\small},
  dotline/.style={densely dotted,line width=0.9pt},
  twig/.style={line width=0.9pt}
]
  % main (infinite) branch indicated by a dotted diagonal
  \draw[dotline] (-0.2,0.2) -- (7.7,-7.7);

  % reference points along the branch for state labels
  \coordinate (A0)   at (0.90,-0.90);
  \coordinate (A1)   at (2.10,-2.10);
  \coordinate (Ak)   at (3.60,-3.60);
  \coordinate (Akp)  at (4.80,-4.80);
  \coordinate (Ak2p) at (6.00,-6.00);
  \coordinate (Ak3p) at (7.10,-7.10);

  % state labels to the right of the branch
  \node[anchor=west] at (A0)   {$q_0$};
  \node[anchor=west] at (A1)   {$q_1$};
  \node[anchor=west] at (Ak)   {$q_k = q$};
  \node[anchor=west] at (Akp)  {$q_{k+p} = q$};
  \node[anchor=west] at (Ak2p) {$q_{k+2p} = q$};
  \node[anchor=west] at (Ak3p) {$q_{k+3p}$};

  % helper: short left-leaning leaf (a small twig) with its label
  \newcommand{\leaf}[2]{%
    \draw[twig] (#1) -- ++(135:0.55);
    \node[anchor=east] at ($(#1)+(135:0.55)$) {#2};
  }

  % twigs (leaves) and inner-node labels a along the branch
  \leaf{0.45,-0.45}{$c$}
  \node[anchor=east] at (0.95,-0.95) {$\scriptstyle a$};

  \leaf{1.55,-1.55}{$c$}
  \node[anchor=east] at (2.55,-2.55) {$\scriptstyle a$};

  \leaf{3.05,-3.05}{$d$}
  \node[anchor=east] at (3.95,-3.95) {$\scriptstyle a$};

  \leaf{4.25,-4.25}{$c/d$}
  \node[anchor=east] at (4.95,-4.95) {$\scriptstyle a$};

  \leaf{5.55,-5.55}{$c$}
  \node[anchor=east] at (6.35,-6.35) {$\scriptstyle a$};

  \leaf{6.95,-6.95}{$c$}
\end{tikzpicture}
\end{document}

