% frame and inner grid
\draw (0,0) rectangle (\N,\N);
\foreach \i in {1,...,8} {
  \draw (\i,0) -- (\i,\N);
  \draw (0,\i) -- (\N,\i);
}
% left-side row labels z_4 ... z_{-4} with arrows toward the grid
\foreach \y/\t in {8.5/{z_4},7.5/{z_3},6.5/{z_2},5.5/{z_1},4.5/{z_0},3.5/{z_{-1}},2.5/{z_{-2}},1.5/{z_{-3}},0.5/{z_{-4}}} {
  \draw[->] (-2.3,\y) -- (0,\y);
  \node[anchor=east] at (-2.35,\y) {$\t$};
}
% dot marking 0 on the z_0 row (placed on first interior vertical line)
\fill (1,4.5) circle[radius=1.2pt];
\node[anchor=west] at (1.15,4.5) {$0$};
% right-going boundary arrows labeled x and y (x above y)
\draw[->] (\N,7.5) -- ++(2.4,0) node[anchor=west] {$x$};
\draw[->] (\N,5.5) -- ++(2.4,0) node[anchor=west] {$y$};

% ---------------- Middle transformation arrow ----------------
