    \draw (\i,0) -- (\i,\N);
    \draw (0,\i) -- (\N,\i);
  }
  % left-side row labels z_{-4} ... z_4 (reversed order) with arrows
  \foreach \y/\t in {8.5/{z_{-4}},7.5/{z_{-3}},6.5/{z_{-2}},5.5/{z_{-1}},4.5/{z_{0}},3.5/{z_{1}},2.5/{z_{2}},1.5/{z_{3}},0.5/{z_{4}}} {
    \draw[->] (-2.3,\y) -- (0,\y);
    \node[anchor=east] at (-2.35,\y) {$\t$};
  }
  % dot at z_0 row (same column as in the left grid)
  \fill (1,4.5) circle[radius=1.2pt];
  \node[anchor=west] at (1.15,4.5) {$0$};
  % right-going boundary arrows labeled -y and -x (with -y above -x)
  \draw[->] (\N,4.5) -- ++(2.4,0) node[anchor=west] {$-y$};
  \draw[->] (\N,2.5) -- ++(2.4,0) node[anchor=west] {$-x$};
\end{scope}
\end{tikzpicture}
\end{document}

