\documentclass{article}
\usepackage{amsmath}
\usepackage{tikz}
\usetikzlibrary{fit,positioning}

\begin{document}

\begin{tikzpicture}[font=\small,>=latex,auto]
  % Styles
  \tikzset{
    mc/.style={draw,minimum width=0.8cm,minimum height=0.75cm,align=center},
    wrapper/.style={draw,inner sep=8pt},
  }

  % Top stream (V_L)
  \node (lblL) {$\text{V}_\mathcal{L}$};
  \node[mc,right=25pt of lblL]        (mc1L) {MC};
  \node[mc,right=20pt of mc1L]        (mc2L) {MC};
  \node[mc,right=20pt of mc2L]        (mc3L) {MC};
  \node[right=2pt of mc3L]            (ellL) {$\cdots$};
  \node[mc,right=0pt of ellL]         (mcNL) {MC};

  % Within-row arrows (top)
  \draw[->] (lblL) -- (mc1L);
  \draw[->] (mc1L) -- node[above=1pt] {$i_{\mathcal{L}_1}$} (mc2L);
  \draw[->] (mc2L) -- node[above=1pt] {$i_{{\text{V}_\mathcal{L}}_2}$} (mc3L);

  % Wrapper (top)
  \node[wrapper,fit=(mc1L)(mc2L)(mc3L)(ellL)(mcNL)] (boxL) {};

  % Bottom stream (V_H)
  \node[below=60pt of lblL] (lblH) {$\text{V}_\mathcal{H}$};
  \node[mc,right=25pt of lblH]        (mc1H) {MC};
  \node[mc,right=20pt of mc1H]        (mc2H) {MC};
  \node[mc,right=20pt of mc2H]        (mc3H) {MC};
  \node[right=2pt of mc3H]            (ellH) {$\cdots$};
  \node[mc,right=0pt of ellH]         (mcNH) {MC};

  % Within-row arrows (bottom)
  \draw[->] (lblH) -- (mc1H);
  \draw[->] (mc1H) -- node[above=1pt] {$i_{\mathcal{H}_1}$} (mc2H);
  \draw[->] (mc2H) -- node[above=1pt] {$i_{{\text{V}_\mathcal{H}}_2}$} (mc3H);

  % Cross-sharing arrows between streams
  \draw[->] (mc1H) -- node[below left=6pt] {$i_{\mathcal{H}_1}$} (mc2L);
  \draw[->] (mc2H) -- node[below left=6pt] {$i_{{\text{V}_\mathcal{H}}_2}$} (mc3L);
  \draw[->] (mc1L) -- node[above left=6pt] {$i_{\mathcal{L}_1}$} (mc2H);
  \draw[->] (mc2L) -- node[above left=6pt] {$i_{{\text{V}_\mathcal{L}}_2}$} (mc3H);

  % Wrapper (bottom)
  \node[wrapper,fit=(mc1H)(mc2H)(mc3H)(ellH)(mcNH)] (boxH) {};

  % Right-side combination
  \node[fit=(mcNL)(mcNH)] (midAlign) {};
  \node[right=30pt of midAlign.east] (oplus) {$\oplus$};
  \node[right=20pt of oplus] (out) {};

  % Outputs to combiner and final output
  \draw[->] (boxL.east) -- node[right=3pt,near end] {$\hat{\imath}_{\text{V}_\mathcal{L}}$} (oplus);
  \draw[->] (boxH.east) -- node[right=3pt,near end] {$\hat{\imath}_{\text{V}_\mathcal{H}}$} (oplus);
  \draw[->] (oplus) -- node[above=2pt] {$\hat{\imath}$} (out);
\end{tikzpicture}

\end{document}

