\draw[solid edge] (a_b_c) -- (a_b);
\draw[solid edge] (a_b) -- (b_d);
\draw[solid edge] (a_b) -- (b);
\draw[solid edge] (b_d) -- (c_d);
\draw[solid edge] (c_d) -- (a_c_d);
\draw[solid edge] (b) -- (b_c_d);
\draw[solid edge] (b_c_d) -- (a_b_c_d);

\end{tikzpicture}
