\documentclass{standalone}
\usepackage{tikz}
\usetikzlibrary{arrows.meta, matrix, positioning}

% Custom styles for nodes and edges
\tikzset{
    every node/.style={
        draw,
        rectangle,
        minimum width=1.2cm,
        minimum height=0.8cm,
        align=center,
        font=\small
    },
    dotted node/.style={
        dashed,
        dash pattern=on 1pt off 1pt
    },
    edge/.style={
        -Stealth,
        thick
    },
    dotted edge/.style={
        dashed,
        thick
    }
}

