\documentclass[tikz,border=3pt]{standalone}
\usepackage{pgfplots}
\usepackage{xcolor}
\usepackage{amsmath}
\usetikzlibrary{arrows.meta,calc}
\pgfplotsset{compat=1.18}

% 可切换字体（可选）
