\usepackage{tikz}
\usepackage{amsmath}
\usepackage{amssymb}

% Define custom colors
\definecolor{lightblue}{RGB}{173, 216, 230}
\definecolor{lightorange}{RGB}{255, 204, 153}
\definecolor{lightpurple}{RGB}{218, 112, 214}

\tikzset{
    every picture/.style={line width=1pt},
    eulerian/.style={ellipse, draw=blue, fill=lightblue, thick},
    lagrangian/.style={ellipse, draw=red, fill=lightorange, thick},
    intersection/.style={ellipse, fill=lightpurple, thick},
}

\begin{document}
\begin{tikzpicture}[scale=1.2]

    % Background rectangle
    \draw[thick] (-4.5, -3.5) rectangle (4.5, 3.5);
    \node at (0, 3) {\textbf{Borel, bounded functions}};

    % Draw the left ellipse (Eulerian sources)
    \draw[eulerian] (-2, 0) ellipse (3 and 2);
    \node[below, text=blue] at (-2, -2) {\textbf{Eulerian sources}};

