\tikzstyle{latent} = [circle, draw, fill=white, text width=0.6cm, text centered];
\tikzstyle{observed} = [rectangle, draw, fill=white, text width=0.6cm, text centered];
\tikzstyle{arrow} = [->, >=stealth];


% Draw the latent states
