\node[latent, right of=X5] (X6) {$\bX_{t+2}$};
\node[latent, right of=X6] (XT) {$\bX_T$};

% Draw the observed states
\node[observed, below of=X1] (O1) {$\by_0$};
\node[observed, below of=X2] (O2) {$\by_{t-2}$};
\node[observed, below of=X3] (O3) {$\by_{t-1}$};
