\draw[arrow] (X3) -- (O3);
\draw[arrow] (X4) -- (O4);
\draw[arrow] (X5) -- (O5);
\draw[arrow] (X6) -- (O6);
\draw[arrow] (XT) -- (OT);

\draw[thick] ($(X3.north west)+(-0.4,0.4)$)  rectangle ($(O5.south east)+(0.4,-0.4)$);
\draw[thick,dotted] ($(X2.north west)+(-0.5,0.5)$)  rectangle ($(O6.south east)+(0.5,-0.5)$);


